import React, { Component } from "react";
import axios from 'axios';
import Table from 'react-bootstrap/Table';
import TaskTableRow from './TaskTableRow';
import Form from 'react-bootstrap/Form'


export default class TaskList extends Component {

    constructor(props) {
        super(props)
        this.state = {
            tasks: ["one"],
            name: ''
        };
        this.onSubmit = this.onSubmit.bind(this);
    }
    onChangeTaskName(e) {
        this.setState({ name: e.target.value })
    }
    onSubmit(e) {
        e.preventDefault()

        const taskObject = {
            name: this.state.name
        };

        axios.post('http://localhost:4000/mentors/create-task/' + this.props.match.params.id, taskObject)
            .then(res => console.log(res.data));

        this.setState({
            name: ''
        });
    }
    componentDidMount() {
        axios.get('http://localhost:4000/tasks/' + this.props.match.params.id)
            .then(res => {
                this.setState({
                    tasks: res.data
                });
            })
            .catch((error) => {
                console.log(error);
            })
    }

    DataTable() {
        return this.state.tasks.map((res, i) => {
            return <TaskTableRow obj={res} key={i} />;
        });
    }


    render() {
        return (
            <div className="table-wrapper">
                <Form onSubmit={this.onSubmit}>
                    <Form.Group controlId="Name">
                        <Form.Label>Name</Form.Label>
                        <Form.Control type="text" value={this.state.name} onChange={this.onChangeTaskName} />
                    </Form.Group>
                    <Button variant="danger" size="lg" block="block" type="submit">
                        Create Task
                    </Button>
                </Form>
                {/* <form onSubmit={this.onSubmit}>
                    <input type="text" value={this.state.name} name="item" className="item" />
                    <button className="btn-add-item">Add</button>
                </form> */}
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Task List</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.DataTable()}
                    </tbody>
                </Table>
            </div>
        );
    }
}
